INSERT INTO public.game (id, created_at, updated_at, name, description, cover_url, developer, release_year) VALUES (3, '2020-10-28 14:35:23.792456+00', '2020-10-28 22:16:33.314229+00', 'Factorio', 'Factorio is an isometric space real-time strategy developed by Wube Software. Story An astronaut is stranded on the surface of a distant unknown planet, that is full with low life forms and fossil fuels. His main objective now is to survive and build a rocket to leave the planet.', 'https://cdn.factorio.com/assets/img/blog/fff-310-factorio-cover-017-stable.png', 'Wube Software', 2018);
INSERT INTO public.game (id, created_at, updated_at, name, description, cover_url, developer, release_year) VALUES (2, '2020-10-28 14:34:04.588142+00', '2020-10-28 22:16:57.741355+00', 'Cyberpunk 2077', 'Cyberpunk 2077 is a science fiction game loosely based on the role-playing game Cyberpunk 2020. Setting The game is set in the year 2077 in a fictional futuristic metropolis Night City in California. In the world of the game, there are developed cybernetic augmentations that enhance people''s strength, agility, and memory. The city is governed by corporations. Many jobs are taken over by the robots, leaving a lot of people poor and homeless. ', 'https://exatcode.com/wp-content/uploads/2019/12/cyberpunk-2077-box-art-01-ps4-us-06jun19.jpg', 'CD Projekt Red', 2020);
INSERT INTO public.game (id, created_at, updated_at, name, description, cover_url, developer, release_year) VALUES (5, '2020-10-28 14:36:47.470211+00', '2020-10-28 22:17:11.140144+00', 'Hades', 'Hades is a rogue-like dungeon crawler that combines the best aspects of Supergiant''s critically acclaimed titles, including the fast-paced action of Bastion, the rich atmosphere and depth of Transistor, and the character-driven storytelling of Pyre. BATTLE OUT OF HELL As the immortal Prince of the Underworld, you''ll wield the powers and mythic weapons of Olympus to break free from the clutches of the god of the dead himself, while growing stronger and unraveling more of the story with each unique escape attempt. ', 'https://www.mobygames.com/images/covers/l/684524-hades-nintendo-switch-front-cover.jpg', 'Supergiant Games', 2014);
